import React from 'react';
import connect from '@vkontakte/vk-connect';
import '@vkontakte/vkui/dist/vkui.css';
import {Panel, Div, Group,Cell, PanelHeader, Button, List, ANDROID, View, FormLayout, Select,
  HeaderButton, ListItem, platform, IOS, } from '@vkontakte/vkui';
import './Persik.css';
import persik from '../img/persik.png';
import Icon28ChevronBack from '@vkontakte/icons/dist/28/chevron_back';
import Icon24Back from '@vkontakte/icons/dist/24/back';
import PropTypes from 'prop-types';
import Clock from './Clock'
import { YMaps, Map, Placemark } from 'react-yandex-maps';

const osname = platform();

function FormattedGeo(props) {
  return <Div> long: {props.data.long}, lat: {props.data.lat}. </Div>
}

class Maps extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      firstEntry: true, // Решает баг с постоянно всплывающим предложением разрешить доступ к гео
      lat: 0,
      long: 0,
      currentGeo: null,
      coordinates: null,
      draggingList: [2, 3, 1, 4, 5],
      activePanel: 'list',
    };

    connect.subscribe((e) => {
      switch (e.detail.type) {
        case 'VKWebAppGeodataResult':
          this.setState({
            lat: e.detail.data.lat,
            long: e.detail.data.long,
            firstEntry: false,
            currentGeo : { center: [e.detail.data.lat, e.detail.data.long], zoom: 15 },
            coordinates : [[e.detail.data.lat, e.detail.data.long]],

          });
          break;
        default:
          console.log("error");
      }
      connect.send("VKWebAppGetGeodata", {});
    });

  }
  componentDidMount() {
    this.timerID = setInterval(
      () => this.tick(),
      1000
    );
  }

  componentWillUnmount() {
    clearInterval(this.timerID);
  }

  tick() {
    connect.send("VKWebAppGetGeodata", {});
  }


  render(){
    return (
      <View activePanel={this.state.activePanel}>
        <Panel id={'list'}>
        <PanelHeader>
        Время и Гео1
        </PanelHeader>

          <Group title="Интересные места">
            {this.state.currentGeo && <YMaps>
              <Div>
                <Map defaultState={this.state.currentGeo}>
                  {this.state.coordinates.map(coordinate => (<Placemark geometry={coordinate} />))}
                </Map>
              </Div>
            </YMaps>}
          </Group>
          <Group>
            <Div>
            <Button size="xl" level="secondary">Построить маршрут</Button>
            </Div>
            <Div>
            <Cell expandable onClick={() => this.setState({ activePanel: 'nothing' })}>Построить маршрут</Cell>
            </Div>


          </Group>



          {this.props.player}
        </Panel>
        <Panel id="nothing">
                 <PanelHeader left={<HeaderButton onClick={() => this.setState({ activePanel: 'list' })}>{osname === IOS ? <Icon24Back/> : <Icon24Back/>}</HeaderButton>}
              left={<HeaderButton onClick={() => this.setState({ activePanel: 'list' })}>Назад</HeaderButton>}
              right={[
                <HeaderButton key="add" onClick={() => {}}><Icon24Back/></HeaderButton>,
                <HeaderButton key="more" onClick={() => {}}><Icon24Back/></HeaderButton>
              ]}
                 >
                   Маршрут
                 </PanelHeader>
                 <Group title="Настройте Ваш маршрут">
                 <FormLayout>
                  <Select top="Количество мест" placeholder="Выберите число мест">
                    <option value="1">1</option>
                    <option value="2">2</option>
                    <option value="3">3</option>
                    <option value="4">4</option>
                    <option value="5">5</option>
                    <option value="6">6</option>
                    <option value="7">7</option>
                    <option value="8">8</option>
                    <option value="9">9</option>
                    <option value="10">10</option>
                  </Select>
                </FormLayout>


                       <List>
                         {this.state.draggingList.map((item) => (
                           <Cell key={item} draggable onDragFinish={({ from, to }) => {
                             const draggingList = [...this.state.draggingList];
                             draggingList.splice(from, 1);
                             draggingList.splice(to, 0, this.state.draggingList[from]);
                             this.setState({ draggingList });
                           }}>{item}</Cell>
                         ))}
                       </List>
                 </Group>
               <div
                 style={{
                   padding: '100px 0',
                   height: '100%',
                   display: 'flex',
                   alignItems: 'center',
                   justifyContent: 'center'
                 }}
               >
                 <div
                   style={{
                     flex: '0 0 auto',
                     color: 'gray',
                     textAlign: 'center'
                   }}
                 >
                   Тут ничего нет<br />
                   <span onClick={() => this.setState({ activePanel: 'list' })} style={{ textDecoration: 'underline' }}>Пошли обратно</span>
                   <span onClick={() => this.setState({ activePanel: 'list' })} style={{ textDecoration: 'underline' }}>Пошли обратно</span>
                   <span onClick={() => this.setState({ activePanel: 'list' })} style={{ textDecoration: 'underline' }}>Пошли обратно</span>
                 </div>
           </div>
       </Panel>
			</View>



    );
  }
}

export default Maps;
